mapper.select:
select board_idx, board_title , memberid , board_content , datetime , viewcnt 
from board.board1
where board_idx = #{board_idx}

mapper.update:
update board.board1 
set board_title = #{board_title}, 
    memberid = #{board_writer}, 
    board_content = #{board_content}
where board_idx = #{board_idx}
